mapper.select:
  		   SELECT max(@rownum)
           FROM visitor
           where 1=1
		       and visitor_date >= (sysdate -#{params.arg1,jdbcType=VARCHAR})
		       and bedroom_name = #{params.arg2,jdbcType=VARCHAR}
		         and reason like #{params.key,jdbcType=VARCHAR}

mapper.delete:
delete from visitor
where visitor_id in

	#{id}
